return array(    'invokables' => array(        'Zhorty\Entity\Trim' => 'Zhorty\Entity\Trim',        'Zhorty\Entity\TrimHydrator' => 'Zend\Stdlib\Hydrator\ClassMethods',    ),    'factories' => array(            'Zhorty\Form\Trim' => function($sm) {            $form = new Form\Trim();            $form->setInputFilter(new Form\TrimFilter());            $form->setHydrator($sm->get('Zhorty\Entity\TrimHydrator'));            $form->bind($sm->get('Zhorty\Entity\Trim'));            return $form;        },    ),);