public function indexAction(){    $sm = $this->getServiceLocator();    $form = $sm->get('Zhorty\Form\Trim');    if ($this->request->isPost()) {        $form->setData($this->request->getPost());        if ($form->isValid()) {            $model = $sm->get('Zhorty\Model\Trim');        	 $model->save($form->getData());        	             return $this->redirect()->toRoute('zhorty-trim');        }          }            return new ViewModel(array('form' => $form));}