public function onRouteCheckForRedirect(MvcEvent $e) {    $uri = $e->getRequest()->getRequestUri();    $uri = ltrim($uri, '/');            $sm = $e->getApplication()->getServiceManager();    $model = $sm->get('Zhorty\Model\Trim');            try {        $origUri = $model->getOrigUriByTrimPath($uri);    }    catch (\Exception $ex) {        return;    }            $locationHeader = new Location();    $locationHeader->setUri($origUri);    $headers = new Headers();    $headers->addHeader($locationHeader);    $response = new Response();    $response->setHeaders($headers);		    $e->setResponse($response);    $e->stopPropagation();}